proc getStyle(name: string): Style =
  if gState.styleSheet.hasKey(name):
    let sc = gState.styleSheet[name]
    result = Style(
      fg: Color(r: sc.fg.r, g: sc.fg.g, b: sc.fg.b),
      bg: Color(r: sc.bg.r, g: sc.bg.g, b: sc.bg.b),
      bold: sc.bold, italic: sc.italic, underline: sc.underline, dim: sc.dim)
  elif gState.styleSheet.hasKey("default"):
    # Use theme's default style as fallback
    let sc = gState.styleSheet["default"]
    result = Style(
      fg: Color(r: sc.fg.r, g: sc.fg.g, b: sc.fg.b),
      bg: Color(r: sc.bg.r, g: sc.bg.g, b: sc.bg.b),
      bold: sc.bold, italic: sc.italic, underline: sc.underline, dim: sc.dim)
  else:
    result = defaultStyle()

proc main() =
  when not defined(emscripten):
    gTerminalState = setupRawMode()
    hideCursor()
    enableMouseReporting()
    enableKeyboardProtocol()
    
    let (w, h) = getTermSize()
    gState = newAppState(w, h)
    
    # Initialize default layer (layer 0)
    gDefaultLayer = addLayer(gState, "default", 0)
    
    # Initialization
    var fontLoaded = figletLoadFont("jazmine")
    var debugMsg = "Font loaded: " & $fontLoaded

    var lastTime = epochTime()
    
    try:
      # Main loop
      while gState.running and gRunning:
        let currentTime = epochTime()
        let deltaTime = currentTime - lastTime
        lastTime = currentTime
        
        # Update FPS counter
        gState.updateFpsCounter(deltaTime)
        
        # Render
        clear()
        
        # Get current time
        var time = now()
        var hour = time.hour
        var minute = time.minute
        var second = time.second
        
        # Format time string
        var timeStr = ""
        if hour < 10:
          timeStr = timeStr & "0"
        timeStr = timeStr & $hour & ":"
        if minute < 10:
          timeStr = timeStr & "0"
        timeStr = timeStr & $minute & ":"
        if second < 10:
          timeStr = timeStr & "0"
        timeStr = timeStr & $second
        
        draw(0, 1, 2, "Time: " & timeStr)
        
        # Try to render
        var lines = figletRender("jazmine", timeStr)
        
        # Center and draw
        if len(lines) > 0:
          var clockWidth = len(lines[0])
          var clockHeight = len(lines)
          
          var startX = 0
          if clockWidth < termWidth:
            var diff = termWidth - clockWidth
            startX = diff / 2
          
          var startY = 10
          if clockHeight < termHeight:
            var diff = termHeight - clockHeight
            startY = diff / 2
          
          var y = startY
          for line in lines:
            draw(0, startX, y, line)
            y = y + 1
        else:
          draw(0, 2, 8, "No lines to render!")

        # Composite layers and display to terminal
        gState.compositeLayers()
        gState.currentBuffer.display(gState.previousBuffer, gState.colorSupport)

        # Frame rate limiting
        if gState.targetFps > 0.0:
          let frameTime = epochTime() - currentTime
          let targetFrameTime = 1.0 / gState.targetFps
          let sleepTime = targetFrameTime - frameTime
          if sleepTime > 0:
            sleep(int(sleepTime * 1000))
    finally:
      # Cleanup terminal
      disableKeyboardProtocol()
      disableMouseReporting()
      showCursor()
      clearScreen()
      restoreTerminal(gTerminalState)
      stdout.write("\n")
      stdout.flushFile()

when isMainModule:
  main()
