proc main() =
  when not defined(emscripten):
    gTerminalState = setupRawMode()
    hideCursor()
    enableMouseReporting()
    enableKeyboardProtocol()
    
    let (w, h) = getTermSize()
    gState = newAppState(w, h)
    
    # Initialize default layer (layer 0)
    gDefaultLayer = addLayer(gState, "default", 0)
    
    # Initialization
    # Track mouse state
    mouseX = 0
    mouseY = 0
    lastButton = "none"
    lastAction = "none"
    clickCount = 0

    var lastTime = epochTime()
    
    try:
      # Main loop
      while gState.running and gRunning:
        let currentTime = epochTime()
        let deltaTime = currentTime - lastTime
        lastTime = currentTime
        
        # Update FPS counter
        gState.updateFpsCounter(deltaTime)
        
        # Poll for input events
        let events = pollInput(gInputParser)
        for nativeEvent in events:
          # Convert to simplified event API
          let event = toSimpleEvent(nativeEvent)
          # Process input event
          # Handle mouse events through the normal input lifecycle
          if event.type == "mouse":
            mouseX = event.x
            mouseY = event.y
            lastButton = event.button
            lastAction = event.action
            
            if event.action == "press":
              clickCount = clickCount + 1
              
              # Check if click was in the box
              if mouseX >= 10 and mouseX <= 29 and mouseY >= 14 and mouseY <= 18:
                print("Box clicked at (" & str(mouseX) & ", " & str(mouseY) & ")")
          
          elif event.type == "mouse_move":
            mouseX = event.x
            mouseY = event.y

        # Render
        # Clear screen
        clear()
        
        # Draw title  
        draw(0, 2, 2, "=== MOUSE EVENT TEST ===")
        
        # Draw instructions
        draw(0, 2, 4, "Move your mouse and click buttons to test")
        draw(0, 2, 5, "Press Q or ESC to quit")
        
        # Display mouse state
        draw(0, 2, 7, "Mouse Position: (" & str(mouseX) & ", " & str(mouseY) & ")")
        draw(0, 2, 8, "Last Button: " & lastButton)
        draw(0, 2, 9, "Last Action: " & lastAction)
        draw(0, 2, 10, "Click Count: " & str(clickCount))
        
        # Draw a clickable box
        draw(0, 10, 13, "+------------------+")
        var i = 0
        while i < 5:
          draw(0, 10, 14 + i, "|                  |")
          i = i + 1
        draw(0, 10, 19, "+------------------+")
        
        draw(0, 13, 16, "CLICK ME!")
        
        # Visual feedback if mouse is over the box
        if mouseX >= 10 and mouseX <= 29 and mouseY >= 14 and mouseY <= 18:
          draw(0, 11, 16, "  HOVERING  ")

        # Composite layers and display to terminal
        gState.compositeLayers()
        gState.currentBuffer.display(gState.previousBuffer, gState.colorSupport)

        # Frame rate limiting
        if gState.targetFps > 0.0:
          let frameTime = epochTime() - currentTime
          let targetFrameTime = 1.0 / gState.targetFps
          let sleepTime = targetFrameTime - frameTime
          if sleepTime > 0:
            sleep(int(sleepTime * 1000))
    finally:
      # Cleanup terminal
      disableKeyboardProtocol()
      disableMouseReporting()
      showCursor()
      clearScreen()
      restoreTerminal(gTerminalState)
      stdout.write("\n")
      stdout.flushFile()
    # Cleanup
    # Cleanup

when isMainModule:
